def shuffleLetter(letter: str):
    if (letter == 'a') :
        return 'b'
    elif (letter == 'b'):
        return 'c'
    elif (letter == 'c'):
        return 'a'
    else:
        return 'a'

compared = {}
    
def nearest(rgb):
    if (rgb in compared):
        return compared[rgb]
    a = list(convert_color(sRGBColor(*rgb), LabColor).get_value_tuple())
    #a = list(color.rgb2lab (np.array([[*rgb]], dtype=np.uint8)))[0]
    #color_diffs = []
    #for color in hexKey:
    ##    
    #    color_diff = delta_e_cie2000(a, color[0])
    #    color_diffs.append((color_diff, color))
    dist = delta_e_cie2000_jit(a, newHexKey)
    closest = newHexKey[np.argmin(dist)]

    ans = [*closest, hexDict[tuple(closest)]]
    
    compared[rgb] = ans
    return ans


def quadKey(x, y, z):
    index = ""
    for i in range(z, 0, -1):
        b = 0
        mask = 1 << (i - 1)
        if ((x & mask) != 0): b +=1
        if ((y & mask) != 0): b += 2
        index += str(b)
    return index


